import { useState } from 'react';

const SimpleInput = props => {
  const [enteredName, setEnteredName] = useState('');
  const [enteredEmail, setEnteredEmail] = useState('');
  const [enteredNameTouched, setEnteredNameTouched] = useState(false);
  const [enteredEmailTouched, setEnteredEmailTouched] = useState(false);
  const [formFormatIsValid, setFormFormatIsValid] = useState(false);

  // const noEmailEntered = enteredEmail.trim() === '' && enteredEmailTouched && !formFormatIsValid;
  // const wrongFormFormat = !formFormatIsValid && enteredEmailTouched;
  const nameInputIsInvalid = enteredName.trim() === '' && enteredNameTouched;
  // const emailInputIsInvalid = noEmailEntered || wrongFormFormat;
  const emailInputIsInvalid = enteredEmail.trim() === '' && enteredEmailTouched && !formFormatIsValid;

  const enteredInputIsValid = !nameInputIsInvalid && !emailInputIsInvalid;

  const emailValidator = email => {
    const regex = /\S+@\S+\.\S+/;
    const result = regex.test(email);

    if (result) {
      setFormFormatIsValid(true);
    }
  };

  let formIsValid = false;

  if (enteredInputIsValid) {
    formIsValid = true;
  }

  const nameInputChangeHandler = e => {
    setEnteredName(e.target.value);
  };

  const emailInputChangeHandler = e => {
    setEnteredEmail(e.target.value);
    emailValidator(enteredEmail);
  };

  const nameInputBlurHandler = e => {
    setEnteredNameTouched(true);
  };

  const emailInputBlurHandler = e => {
    setEnteredEmailTouched(true);
  };

  const formSubmissionHandler = e => {
    e.preventDefault();

    setEnteredNameTouched(true);

    if (!enteredInputIsValid) {
      return;
    }

    setEnteredName('');
    setEnteredEmail('');
    setEnteredNameTouched(false);
    setEnteredEmailTouched(false);
  };

  const nameInputClasses = nameInputIsInvalid
    ? 'form-control invalid'
    : 'form-control';

  const emailInputClasses = emailInputIsInvalid
    ? 'form-control invalid'
    : 'form-control';

  return (
    <form onSubmit={formSubmissionHandler}>
      <div className={nameInputClasses}>
        <label htmlFor='name'>Your Name</label>
        <input
          type='text'
          id='name'
          onChange={nameInputChangeHandler}
          onBlur={nameInputBlurHandler}
          value={enteredName}
        />
        {nameInputIsInvalid && <p className='error-text'>Invalid name.</p>}
      </div>
      <div className={emailInputClasses}>
        <label>Email</label>
        <input
          type='text'
          id='email'
          onChange={emailInputChangeHandler}
          onBlur={emailInputBlurHandler}
          value={enteredEmail}
        />
        {emailInputIsInvalid && <p className='error-text'>Invalid email.</p>}
      </div>
      <div className='form-actions'>
        <button disabled={!formIsValid}>Submit</button>
      </div>
    </form>
  );
};

export default SimpleInput;
