import useInputNew from '../hooks/use-input-new';

const BasicForm = props => {
  const {
    value: enteredName,
    hasError: nameHasError,
    valueIsValid: nameIsValid,
    onChangeHandler: nameChangeHandler,
    onBlurHandler: nameBlurHandler,
    reset: nameReset,
  } = useInputNew(value => value.trim() !== '');

  const {
    value: enteredLastName,
    hasError: lastNameHasError,
    valueIsInvalid: lastNameValid,
    onChangeHandler: lastNameChangeHandler,
    onBlurHandler: lastNameBlurHandler,
    reset: lastNameReset,
  } = useInputNew(value => value.trim() !== '');

  const {
    value: enteredEmail,
    hasError: emailHasError,
    valueIsInvalid: emailIsValid,
    onChangeHandler: emailChangeHandler,
    onBlurHandler: emailBlurHandler,
    reset: emailReset,
  } = useInputNew(value => {
    const regex = /\S+@\S+\.\S+/;
    const result = regex.test(value);
    return result;
  });

  const formIsValid = false;

  if (nameIsValid && lastNameValid && emailIsValid) {
    formIsValid = true;
  }

  const onSubmitFormHandler = () => {
    if (formIsValid) {
      nameReset();
      lastNameReset();
      emailReset();
    }
    return;
  };

  return (
    <form onSubmit={onSubmitFormHandler}>
      <div className='control-group'>
        <div className={nameHasError ? 'form-control invalid' : 'form-control'}>
          <label htmlFor='name'>First Name</label>
          <input
            type='text'
            id='name'
            onChange={nameChangeHandler}
            onBlur={nameBlurHandler}
            value={enteredName}
          />
          {nameHasError ? <p>Please enter a valid name.</p> : null}
        </div>
        <div
          className={lastNameHasError ? 'form-control invalid' : 'form-control'}
        >
          <label htmlFor='name'>Last Name</label>
          <input
            type='text'
            id='name'
            onChange={lastNameChangeHandler}
            onBlur={lastNameBlurHandler}
            value={enteredLastName}
          />
          {lastNameHasError ? <p>Please enter a valid name.</p> : null}
        </div>
      </div>
      <div className={emailHasError ? 'form-control invalid' : 'form-control'}>
        <label htmlFor='name'>E-Mail Address</label>
        <input
          type='email'
          id='email'
          onChange={emailChangeHandler}
          onBlur={emailBlurHandler}
          value={enteredEmail}
        />
        {emailHasError ? <p>Please enter a valid name.</p> : null}
      </div>
      <div className='form-actions'>
        <button>Submit</button>
      </div>
    </form>
  );
};

export default BasicForm;
