import useInputNew from '../hooks/use-input-new';

const BasicForm = props => {
  const {
    value: enteredName,
    valueIsValid: nameIsValid,
    valueIsInvalid: name ,
    onChangeHandler,
    onBlurHandler,
    onSubmithandler,
    reset,
  } = useInputNew();

  return (
    <form onSubmit={onSubmitHandler}>
      <div className='control-group'>
        <div className={nameIsValid ? 'form-control invalid' : 'form-control'}>
          <label htmlFor='name'>First Name</label>
          <input
            type='text'
            id='name'
            onChange={nameChangeHandler}
            onBlur={nameBlurHandler}
            value={enteredName}
          />
        </div>
        <div
          className={lastNameIsValid ? 'form-control invalid' : 'form-control'}
        >
          <label htmlFor='name'>Last Name</label>
          <input
            type='text'
            id='name'
            onChange={lastNameChangeHandler}
            onBlur={lastNameBlurHandler}
            value={enteredLastName}
          />
        </div>
      </div>
      <div className={emailIsValid ? 'form-control invalid' : 'form-control'}>
        <label htmlFor='name'>E-Mail Address</label>
        <input
          type='email'
          id='email'
          onChange={emailChangeHandler}
          onBlur={emailBlurHandler}
          value={enteredEmail}
        />
      </div>
      <div className='form-actions'>
        <button>Submit</button>
      </div>
    </form>
  );
};

export default BasicForm;
